Script.script:
function update_Vitals()
	hive = hive or {}
	hive.Char = hive.Char or {}
	hive.Char.Vitals = hive.Char.Vitals or {}

hpbar = Geyser.Gauge:new({
  name="hpbar",
  x="50%", y="85%", -- CHANGE THIS TO MOVE
  width="45%", height="20px", -- CHANGE THIS RESIZE
})
hpbar.front:setStyleSheet([[background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #98f041, stop: 0.1 #8cf029, stop: 0.49 #66cc00, stop: 0.5 #52a300, stop: 1 #66cc00);
    border-top: 1px black solid;
    border-left: 1px black solid;
    border-bottom: 1px black solid;
    border-radius: 7;
    padding: 3px;
]])
hpbar.back:setStyleSheet([[background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #78bd33, stop: 0.1 #6ebd20, stop: 0.49 #4c9900, stop: 0.5 #387000, stop: 1 #4c9900);
    border-width: 1px;
    border-color: black;
    border-style: solid;
    border-radius: 7;
    padding: 3px;
]])
mpbar = Geyser.Gauge:new({
  name="mpbar",
  x="50%", y="90%", -- CHANGE THIS TO MOVE
  width="45%", height="20px", -- CHANGE THIS RESIZE
})
mpbar.front:setStyleSheet([[background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #f04198, stop: 0.1 #f0298c, stop: 0.49 #cc0066, stop: 0.5 #a30052, stop: 1 #cc0066);
    border-top: 1px black solid;
    border-left: 1px black solid;
    border-bottom: 1px black solid;
    border-radius: 7;
    padding: 3px;
]])
mpbar.back:setStyleSheet([[background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #78bd33, stop: 0.1 #6ebd20, stop: 0.49 #4c9900, stop: 0.5 #387000, stop: 1 #4c9900);
    border-width: 1px;
    border-color: black;
    border-style: solid;
    border-radius: 7;
    padding: 3px;
]])
spbar = Geyser.Gauge:new({
  name="spbar",
  x="50%", y="95%", -- CHANGE THIS TO MOVE
  width="45%", height="20px", -- CHANGE THIS RESIZE
})
spbar.front:setStyleSheet([[background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #f09841, stop: 0.1 #f08c29, stop: 0.49 #cc6600, stop: 0.5 #a30052, stop: 1 #cc6600);
    border-top: 1px black solid;
    border-left: 1px black solid;
    border-bottom: 1px black solid;
    border-radius: 7;
    padding: 3px;
]])
spbar.back:setStyleSheet([[background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #78bd33, stop: 0.1 #6ebd20, stop: 0.49 #4c9900, stop: 0.5 #387000, stop: 1 #4c9900);
    border-width: 1px;
    border-color: black;
    border-style: solid;
    border-radius: 7;
    padding: 3px;
]])

hpbar:setValue((100/gmcp.Char.Vitals.maxhp)*gmcp.Char.Vitals.hp,100,"HP"..gmcp.Char.Vitals.maxhp.."/"..gmcp.Char.Vitals.hp)
mpbar:setValue((100/gmcp.Char.Vitals.maxmp)*gmcp.Char.Vitals.mp,100,"MP"..gmcp.Char.Vitals.maxmp.."/"..gmcp.Char.Vitals.mp)
spbar:setValue((100/gmcp.Char.Vitals.maxsp)*gmcp.Char.Vitals.sp,100,"MP"..gmcp.Char.Vitals.maxsp.."/"..gmcp.Char.Vitals.sp)
end

registerAnonymousEventHandler("gmcp.Char.Vitals", "update_Vitals")
